export = Config;

class ConfigPart {
}

class ConfigPartService extends ConfigPart {
  host: string;
  port?: number;
  namespace?: string;
  serviceUrl: string;
}

class ConfigPartDatabase extends ConfigPart {
  user: string;
  password: string;
  host: string;
  port: string;
  postgres: string;
  owner: string;
  databaseUrl: string;
}

declare class Config {
  set: any;

  get(key: any, callback: any): any;

  service(key: string): ConfigPartService;

  database(): ConfigPartDatabase;
}
